mapper.select:
/* selectAdminMemberList [관리자 회원 리스트 조회 쿼리] */ SELECT d."DEPT_NM", a.* FROM "DEPT_INFO_TB" d RIGHT JOIN "MNGR_MBR_INFO_TB" a ON d."DEPT_NO" = a."DEPT_NO" LEFT JOIN "MENU_AUTHRT_INFO_TB" m ON a."DEPT_NO" = m."DEPT_NO"
WHERE a."USE_YN" = 'Y'
AND m."AUTHRT_NO" = #{authNo}
AND a."MNGR_ID" LIKE '%'||#{searchKeyword}||'%'
AND a."MNGR_NM" LIKE '%'||#{searchKeyword}||'%'
ORDER BY a."FRST_RGTR_ID" DESC
