mapper.select:
/* selectEntMemberList [기업 회원 리스트 조회 쿼리] */ SELECT EMIT."ENT_MBR_NO", EMIT."CONM_NM", EMIT."BR_NO", EMIT."RPRSV_NM", EMIT."PIC_NM", EMIT."PIC_MBL_TELNO", EMIT."EML_ADDR", EMIT."FRST_REG_DT" FROM "ENT_MBR_INFO_TB" EMIT
AND EMIT."CONM_NM" = #{conmNm}
AND EMIT."BR_NO" = #{brNo}
AND EMIT."RPRSV_NM" LIKE '%'||#{searchKeyword}||'%'
AND EMIT."PIC_NM" LIKE '%'||#{searchKeyword}||'%'
ORDER BY EMIT."FRST_REG_DT" DESC

mapper.delete:
/* deleteEntMember [기업 회원 삭제 쿼리] */
DELETE
FROM
  "ENT_MBR_INFO_TB"
WHERE
  "ENT_MBR_NO" = #{no}
