mapper.select:
/* selectEntMemberList [기업 회원 리스트 조회 쿼리] */ SELECT GMIT."MBR_NO", GMIT."MBR_NM", GMIT."MBR_ID", GMIT."MBR_MBL_TELNO", GMIT."EML_ADDR", GMIT."FRST_REG_DT", GMIT."LAST_MDFCN_DT", COALESCE(CASE WHEN EPMIT."ALW_YN" = 'N' OR EPMIT."DEL_YN" = 'Y' THEN NULL ELSE EMIT."CONM_NM" END, '-') AS "CONM_NM" -- CONM_NM이 NULL일 경우 '-'로 대체 FROM "GEN_MBR_INFO_TB" GMIT LEFT JOIN "ENT_PRCPT_MBR_INFO_TB" EPMIT ON GMIT."MBR_NO" = EPMIT."MBR_NO" LEFT JOIN "ENT_MBR_INFO_TB" EMIT ON EPMIT."ENT_MBR_NO" = EMIT."ENT_MBR_NO"
AND EMIT."CONM_NM" = #{conmNm}
AND GMIT."MBR_NM" LIKE '%'||#{searchKeyword}||'%'
AND GMIT."MBR_ID" LIKE '%'||#{searchKeyword}||'%'
ORDER BY GMIT."FRST_REG_DT" DESC

mapper.delete:
/* deleteGenMember [일반 회원 삭제 쿼리] */
DELETE
FROM
    "GEN_MBR_INFO_TB"
WHERE
    "MBR_NO" = #{no}

mapper.update:
/* updateEntPrcpt [일반 회원 수정 - 기업 소속 업데이트 쿼리] */
UPDATE
    "ENT_PRCPT_MBR_INFO_TB"
SET
    ENT_MBR_NO = #{entMbrNo}
WHERE
    MBR_NO = #{mbrNo}

/* resetPassword [일반회원 비밀번호 초기화 쿼리] */
UPDATE
    "GEN_MBR_INFO_TB"
SET
    "MBR_PSWD" = #{mbrPswd},
    "LAST_MDFCN_DT" = #{lastMdfcnDt},
    "LAST_MDFR_ID" = #{lastMdfrId}
WHERE
    "MBR_ID" = #{mbrId}
